import React, { useEffect, useState } from 'react';
import { View, FlatList, Pressable, Text, StyleSheet, Image, ActivityIndicator, SafeAreaView, TouchableOpacity } from 'react-native';
import { FontAwesome5 } from '@expo/vector-icons';
import { useGetViviendaQuery } from '../services/ecApi';
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, getDoc, doc } from 'firebase/firestore';
import CardAdmin from './CardAdmin';
import { useNavigation } from '@react-navigation/native';
import { storeData, retrieveData } from '../redux/storageService'; // Importa las funciones de AsyncStorage

const SelectHouseScreen = ({ route }) => {
  const { data: vivienda, isLoading } = useGetViviendaQuery();
  const [userRole, setUserRole] = useState(null);
  const navigation = useNavigation();

  useEffect(() => {
    const fetchData = async () => {
      const storedUserRole = await retrieveData('userRole');
      setUserRole(storedUserRole);
    };

    fetchData(); // Carga inicial de datos desde AsyncStorage
  }, []);

  useEffect(() => {
    const auth = getAuth();
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      if (user) {
        try {
          const db = getFirestore();
          const userDoc = await getDoc(doc(db, 'usuarios', user.uid));
          const fetchedUserData = userDoc.data();

          // Assuming your user data has a 'userRole' field
          const role = fetchedUserData?.userRole; // Use optional chaining to handle undefined
          setUserRole(role);

          // Almacena el rol del usuario en AsyncStorage para persistencia
          storeData('userRole', role);
        } catch (error) {
          console.error('Error fetching user data:', error);
        }
      }
    });

    return () => unsubscribe();
  }, []);

  const handleHouseSelection = (house) => {
    navigation.navigate('SelectPatients', { house });
  };

  const renderHouseButton = ({ item }) => (
    <TouchableOpacity
      style={styles.button}
      key={item.id}
      onPress={() => handleHouseSelection(item.nombre)}
    >
      <FontAwesome5 name="house-user" size={24} color="black" />
      <Text style={styles.houseName}>{item.nombre}</Text>
    </TouchableOpacity>
  );

  return (
    <SafeAreaView style={styles.container}>
      {isLoading ? (
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="small" color="#5fbcc0" />
        </View>
      ) : (
        <>
          <Image
            source={{
              uri: 'data:image/png;base64,' }}
            style={styles.logo}
          />
  
          {/* Render based on userRole */}
          {userRole === 'admin' ? (
            <>
              <Text style={styles.welcomeText}>{`vivienda asistida`}</Text>
              <CardAdmin userRole={userRole} />
            </>
          ) : userRole === 'operator' ? (
            <>
              <Text style={styles.welcomeText}>{`Bienvenido, operador`}</Text>
            </>
          ) : userRole === null ? (
            <>
              <ActivityIndicator size="small" color="#5fbcc0" />
            </>
          ) : (
            <>
              <Text style={styles.welcomeText}>{`Bienvenido selecciona una vivienda para comenzar la jornada laboral`}</Text>
              <FlatList
                data={vivienda}
                renderItem={renderHouseButton}
                keyExtractor={(item) => item.id.toString()}
                numColumns={2}
              />
            </>
          )}
        </>
      )}
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    marginTop: 50,
    flex: 1,
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    // backgroundColor: 'white',
  },
  welcomeText: {
    fontSize: 18,
    marginBottom: 10,
    textAlign: 'center',
    marginTop: 10,
  },
  button: {
    margin: 10,
    borderRadius: 10,
    overflow: 'hidden',
    borderColor: '#ccc',
    alignItems: 'center',
    justifyContent: 'center',
    padding: 10,
    width: '44%',
    height: 200,
    backgroundColor: 'white',
    borderWidth: 2,
    borderColor: "#5fbcc0",
  },
  logo: {
    width: 100,
    height: 100,
    marginBottom: 20,
    borderRadius: 50,
    alignContent: 'center',
    alignSelf: 'center',
    marginTop: 25,
  },
  houseName: {
    marginTop: 5,
    textAlign: 'center',
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default SelectHouseScreen;